import React, { Component } from "react";

import api from "../services/api";

export default class Movie extends Component {

  constructor(props) {
    super(props);

    this.state = {
      fullTitle: "",
      image: "",
      imDbRating: "",
      year: "",
    };
  }

  componentDidMount() {
    api.getMovies()
      .then(res => {
        this.setState({ movies: res.data });
      })
      .catch(err => console.log(err));
  }

  componentDidUpdate() {
    console.log(this.state.movies);
  }

  render() {
    return (
      <div>
        <h1>Movie List</h1>
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Image</th>
              <th>IMDb Rating</th>
              <th>Year</th>
            </tr>
          </thead>
          <tbody>
            {this.state.movies.map(movie => (
              <tr key={movie.id}>
                <td>{movie.title}</td>
                <td>{movie.image}</td>
                <td>{movie.imDbRating}</td>
                <td>{movie.year}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }

}
