  const [imDbRating, setImDbRating] = useState([]);
  const [year, setYear] = useState([]);

  useEffect(() => {
    api.get("/movie").then((response) => {
      setMovie(response.data);
    });
  }, []);

  return (
    <div className="movie">
      <h1>{movie.title}</h1>
      <p>{movie.overview}</p>
    </div>
  );
}



