  //       imDbRating: this.props.movie.imDbRating,
  //       year: this.props.movie.year,
  //     });
  //   }
  // }

  render() {
    return (
      <div>
        <img src={this.state.image} alt="movie" />
        <div>
          <h5>{this.state.fullTitle}</h5>
          <p>{this.state.imDbRating}</p>
          <p>{this.state.year}</p>
        </div>
      </div>
    );
  }

}
