const { createSlice } = require('@reduxjs/toolkit');

const initialState = [];

const addMovieReducer = createSlice({
  name: 'movies',
  initialState,
  reducers: {
    addMovie: (state, action) => {
      state.push(action.payload);
      return state;
    }
  }
});

export const { 
  addMovie 
} = addMovieReducer.actions;

export default addMovieReducer.reducer;
